        /**
         * Verify that the object not null
         * 
         * @param objects
         * @return
         */
        public bool objectNotNull(Object[] objects)
        {
            int size = objects.Length;
            for (int i = 0; i < size; i++)
            {
                if (objects[i] == null)
                {
                    return false;
                }
            }
            return true;
        }

        /**
         * Verify that the string not
         * 
         * @param strings
         * @return
         */
        public bool stringsNotNull(String[] strings)
        {
            int size = strings.Length;
            for (int i = 0; i < size; i++)
            {
                if (strings[i] == null || strings[i].Equals("") || strings[i].Equals("null") || strings[i].Equals("undefined") || strings[i].Equals("[]"))
                {
                    return false;
                }
            }
            return true;
        }

        public bool stringsNotNull(String st)
        {
            if (st == null || st.Equals("") || st.Equals("null") || st.Equals("undefined") || st.Equals("[]"))
            {
                return false;
            }
            return true;
        }


