        public bool isDouble(String[] strings)
        {
            int size = strings.Length;
            for (int i = 0; i < size; i++)
            {
                try
                {
                    Double.Parse(strings[i]);
                }
                catch (Exception numberFormatException)
                {
                    return false;
                }
            }
            return true;
        }

        /**
         * Verify that Integer not null or 0
         * 
         * @param integers
         * @return
         */
        public bool integerNotNull_Zero(int[] integers)
        {
            int size = integers.Length;
            for (int i = 0; i < size; i++)
            {
                if (integers[i] == null || integers[i] == 0)
                {
                    return false;
                }
            }
            return true;
        }

