        public bool listNotNull_Zero(List<String> l)
        {
            int size = l.Count;
            for (int i = 0; i < size; i++)
            {
                if (l[i] == null || (l[i] != null && l[i].Length == 0))
                {
                    return false;
                }
            }
            return true;
        }

